import "./signup.css";
import axios from "axios";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
import { useState } from "react";
export default function SignupPage() {
  const navigate = useNavigate();
  const { authDispatch } = useAuth();
  const [userData, setUserData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    passwordsDifferent:false
  })

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value })
  }

  const checkPasswordHandler = () => {
    if (user.password !== user.confirmPassword) {
      alert("Your password are not similar");
    } else {
      return true;
    }
  }

  const signUpHandler = async (e) => {
    e.preventDefault();
    if (checkPasswordHandler()) {
      try {
        const response = await axios.post("/api/auth/signup", user);
        if (response.status === 201) {

          localStorage.setItem("token", response.data.encodedToken);
          localStorage.setItem("user", JSON.stringify(response.data.createdUser));

          authDispatch({ type: "SIGNUP", payload: { user: response.data.createdUser, token: response.data.encodedToken } })

          navigate("/");
        }
        else if (response.status === 422) {
          alert("User already exists");
        }
        else if (response.status === 500) {
          alert("Server Error");
        }
      }
      catch (error) {
        console.error(error);
      }
    }
  }

  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>SignUp</h2>
        <h4>Please fill in this form to create an account.</h4>
        <form>
        <div className="user-box">
            <input type="text" name="text"value={user.firstName} onChange={changeHandler}/>
            <label>First Name</label>
          </div>
          <div className="user-box">
            <input type="text" name="text" value={user.lastName} onChange={changeHandler}/>
            <label>Last Name</label>
          </div>
          <div className="user-box">
            <input type="email" name="gmail" value={user.email} onChange={changeHandler}/>
            <label>Gmail</label>
          </div>
          <div className="user-box">
            <input type="password" name="password" value={user.password} onChange={changeHandler}/>
            <label>Create Password <i class="fa-solid fa-eye-slash"></i></label>
          </div>
          <div className="user-box">
            <input type="password" name="password"value={user.confirmPassword} onChange={changeHandler} />
            <label>Confirm Password <i class="fa-solid fa-eye"></i><i class="fa-solid fa-eye-slash"></i></label>
          </div>
          <p className="form-terms">
            <input type="checkbox" />
            By creating an account you agree to{" "}
            <a href="sample.com">our Terms</a> & Privacy.
          </p>
          <button type="submit" className="signup-btn"onClick={signUpHandler}>
            SignUp
          </button>
          <Link to="/login">
          <button className="already-account">Already have an account </button>
          </Link>
        </form>
      </div>
    </div>
  );
}
