import "./signup.css";
import { Link ,useNavigate} from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
export default function SignupPage() {
  const [userData, setUserData] = useState({
    firstName:"",
    lastName:"",
    email: "",
    password: "",
    confirmPassord:""
  })
    const {setUser} = useAuth()
    const navigate=useNavigate()
    const changeHandler = (e) => {
        setUserData((prev) =>  ({...prev, [e.target.name]: e.target.value})) 
    }
    const signupHandler = async () => {
      try {
        const response = await axios.post("/api/auth/signup", userData);
        setUser({
          users: response.data.createdUser,
          token: response.data.encodedToken,
        });
        navigate("/");
      } catch (error) {
        console.log(error);
      }
    };
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>SignUp</h2>
        <h4>Please fill in this form to create an account.</h4>
        <form>
          <div className="user-box">
            <input 
            type="text"
             name="firstName" 
             onChange={(e) => changeHandler(e)}
             value={userData.firstName}
             />
            <label>First Name</label>
          </div>
          <div className="user-box">
            <input 
            type="text" 
            name="lastName"
            value={userData.lastName}
            onChange={(e) => changeHandler(e)}
             />
            <label>Last Name</label>
          </div>
          <div className="user-box">
            <input 
            type="email" 
            name="gmail" 
            value={userData.email}
            onChange={(e) => changeHandler(e)}
            />
            <label>Gmail</label>
          </div>
          <div className="user-box">
            <input
             type="password" 
             name="password" 
             value={userData.password}
            onChange={(e) => changeHandler(e)}
             />
            <label>Create Password</label>
          </div>
          <div className="user-box">
            <input 
            type="password"
            name="confirmPassword" 
            value={userData.confirmPassord}
            onChange={(e) => changeHandler(e)}
            />
            <label>Confirm Password</label>
          </div>
          <p className="form-terms">
            <input type="checkbox" />
            By creating an account you agree to{" "}
            <a href="sample.com">our Terms</a> & Privacy.
          </p>
          {userData.password===userData.confirmPassord ?
          <button
          type="submit"
          className="signup-btn"
          onClick={signupHandler}
           >
           SignUp
         </button>
         :
         "password not similar"
        }
          <Link to="/login">
          <button className="already-account">Already have an account </button>
          </Link>
        </form>
      </div>
    </div>
  );
}
