import { useAuth,useCart } from "../../../hooks";
import { removeFromCartHandler } from "../../../hooks/utilis/cart-utils/cart-util";
const CartAmount = ({ numberItems,cartPrice,cartDiscount,cartTotalPrice ,cartDelivaryCharge,cartId}) => {
  const {authState:{token}}=useAuth();
  const {cartDispatch}=useCart();
  const callRemoveFromCartHandler = (_id) => {
    removeFromCartHandler(_id, token, cartDispatch)
  }
  return (
      <>
        <div className="price-data">
          <div>
            <span>Price({numberItems} items)</span>
            <span>Rs.{cartPrice}</span>
          </div>
          <div>
            <span>Disount</span>
            <span>Rs.{cartDiscount}</span>
          </div>
          <div>
            <span>Delivery Charges</span>
            <span>Rs.{cartDelivaryCharge}</span>
          </div>
        </div>
        <hr />
        <h3>
          your total :Rs.<span className="cart-total">{cartTotalPrice}</span>
        </h3>
        <hr />
        <p className="savings">you will save Rs.{cartDiscount} on this order</p>
        <button className="clear-cart banner-btn1"onClick={()=>callRemoveFromCartHandler(cart)}>clear cart</button>
        <button className="place-order banner-btn1">place-order</button>
      </>
    );
  };
  export { CartAmount };
