import React,{useEffect} from "react";
import "./wishlist.css";
import { WishlistCard } from "./wishlist_card/Wishlistcard";
import { useAuth,useCart,useWishlist } from "../../hooks";
import { moveToCartandler,getWishlistHandler,removeFromWishlistHandler } from "../../hooks/utilis";
export default function Wishshlist() {
  const {authState:token}=useAuth();
  const {cartState,cartDispatch}=useCart();
  const {wishlistState:wishlist,wishlistDispatch}=useWishlist();
  const callRemoveFromWishlistHandler=(_id)=>{
    removeFromWishlistHandler(_id,token,wishlistDispatch)
  }
  const callMoveToCartHandler=()=>{
    const item = wishlist.find(item => item._id === _id);
    moveToCartandler(_id, item, token, cartState, cartDispatch);
    removeFromWishlistHandler(_id, token, wishlistDispatch);
  }
  useEffect(() =>getWishlistHandler(token,wishlistDispatch),[]);
  return (
    <section className="products">
      <div className="section-title">
        <h2>My WishList {wishlist.length}</h2>
      </div>
      <div className="products-center">
        {wishlist.map(({
           _id,
           productImg,
           imgAlt,
           title,
           description,
           productPrice,
           productId,
           callRemoveFromWishlistHandler,
           callMoveToCartHandler
        })=>(
          <WishlistCard
          key={productId}
          productId={productId}
          productImg={productImg}
          imgAlt={"product"}
          title={title}
          description={description}
          productPrice={productPrice}
        />
        
        ))}
        
      </div>
    </section>
  );
}
