import "../authentication.css";
import { Link } from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
import {useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function loginPage() {
  const {authDispatch}=useAuth();
  const [user,setUser]=useState({
    email:"",
    password:""
  })
  const navigate=useNavigate();
  const guestUser = {
    email: "app@gmail.com",
    password: "app123"
  }

  const changeHandler = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value })
  }

  const guestUserHandler = (event) => {
    event.preventDefault();
    setUser(guestUser);
  }
  const loginHandler = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("/api/auth/login", user);
      if (response.status === 200) {

        localStorage.setItem("token", response.data.encodedToken);
        localStorage.setItem("user", JSON.stringify(response.data.foundUser));

        authDispatch({ type: "LOG_IN", payload: { user: response.data.foundUser, token: response.data.encodedToken } })

        navigate("/");
      }
      else if (response.status === 404) {
        alert("Email not found");
      }
      else if (response.status === 401) {
        alert("Wrong Password");
      }
      else if (response.status === 500) {
        alert("Server Error");
      }
    }
    catch (error) {
      console.log(error);
    }
  }
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input 
            type="email" 
            name="useremail" 
            placeholder="enter email"
           value={user.email}
           onChange={changeHandler}
            />
            <label>Email address </label>
          </div>
          <div className="user-box">
            <input 
            type="password"
             name="password" 
             value={user.password}
             onChange={changeHandler}
             />
            <label>Password</label>
          </div>
          <div className="password-box">
            <div className="div-inline">
              <div className="password-save">
                <input type="checkbox" name="remember-me" />
                <label htmlFor="remember-me">Remember me</label>
              </div>
              <Link to="/forgot-password">
              <div className="forgot-pwd">
                <span className="forgot-label">forgot password ?</span>
              </div>
              </Link>
            </div>
            <button 
            type="submit" 
            className="login-submit"
           onClick={guestUserHandler}
            >
            Add Guest credentials
            </button>
            <button 
            type="submit" 
            onClick={loginHandler}
            >
              Login
            </button>
            <Link to="/signup">
            <button className="create-account">Create New Account </button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}
