      <section className="products-list">
        <div className="section-title">
          <h2>our products</h2>
        </div>
        <div className="products-middle">
          {filteredData.map(({
             _id,
             image,
             name,
             subtitle,
             rating,
             price,
             discount,
        })=>(
          <ProductsList
            key={_id}
            productFilterImg={image}
            imgAlt={"product"}
            title={name}
