import "./product-filter.css";
import {
  ProductsList
} from "./components/product-all";
import {
  Productfilter
} from "./components/product-filter";
import axios from 'axios';
import React, {
  useEffect,
  useState
} from "react";
export default function ProductWithFilter() {
  const [productData, setProductData] = useState([]);
  useEffect(() => {
    (async function () {
      try {
        await axios.get(`/api/products`).then((res) => {
          setProductData(res.data.products);
        });
      } catch (error) {
        console.error(error);
        setProductData("failed do fetch data", error);
      }
    })();
  }, []);
  return ( <
    div className = "filter-page" >
    <
    section className = "filter-product-container" >
    <
    Productfilter / >
    <
    /section> <
    section className = "products" >
    <
    div className = "section-title" >
    <
    h2 > our products < /h2> <
    /div> <
    div className = "products-center" > {
      products.map(({
        _id,
        name,
        subtitle,
        rating,
        price,
        discount,
        image
      }) => {
        <
        ProductsList
        key = {
          _id
        }
        productFilterImg = {
          image
        }
        imgAlt = {
          "product"
        }
        title = {
          name
        }
        productSubtitle = {
          subtitle
        }
        starRating = {
          rating
        }
        productPrice = {
          price
        }
        productDiscount = {
          discount
        }
        />
      })
    } <
    /div> <
    /section> <
    /div>
  );
}
