import { getSingleProductService } from "../../../services/product-service/single-product.service";

export const getSingleProductHandler=async (productId,setProducts)=>{
    try {
        const res=await getSingleProductService(productId);
        if(res.status===200){
            const {data:{product}}=res;
            setProducts(product)
        }else{
            throw new Error();
        }
    } catch (error) {
        console.error(error)
    }
}
