import "../authentication.css";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
import { useAuth } from "../../../hooks/context/auth-context";
export default function loginPage() {
  const [user, setUser] = useState({ email: "", password: "" });
	const { setIsAuth, setToken, navigate } = useAuth();
	const loginHandler = async (e, user) => {
		e.preventDefault();

		try {
			const response = await axios.post("/api/auth/login", user);
			localStorage.setItem("isAuth", true);
			localStorage.setItem("token", response.data.encodedToken);
			setToken(response.data.encodedToken);
			setIsAuth(true);
			setLoading(false);
			navigate("/");
		} catch (err) {
			console.error( err);
		}
	};
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={(e) => loginHandler(e, user)}>
          <div className="user-box">
            <input 
            type="email" 
            name="useremail"
            value={user.email}
            onChange={(e) => setUser((prev) => ({ ...prev, email: e.target.value }))}
             />
            <label>Email address </label>
          </div>
          <div className="user-box">
            <input 
            type="password" 
            name="password" 
            value={user.password}
						onChange={(e) => setUser((prev) => ({ ...prev, password: e.target.value }))}
            />
            <label>Password</label>
          </div>
          <div className="password-box">
            <div className="div-inline">
              <div className="password-save">
                <input type="checkbox" name="remember-me"checked />
                <label for="remember-me">Remember me</label>
              </div>
              <Link to="/forgot-password">
              <div className="forgot-pwd">
                <span className="forgot-label">forgot password ?</span>
              </div>
              </Link>
            </div>
            <button 
            type="submit"
             className="login-submit"
             onClick={(e) => {
              setUser({
                email: "adarshbalika@gmail.com",
                password: "adarshbalika",
              });
            }}
             >
            Add Guest credentials
            </button>
            <button 
            type="submit"
             className="login-submit">
              Login
            </button>
            <Link to="/signup">
            <button className="create-account">Create New Account </button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}
