import "./product-filter.css";
import axios from 'axios';
import React, { useEffect, useState } from "react";
import { ProductsList } from "./components/product-all";
import { Productfilter } from "./components/product-filter";
import { GetFilteredData, GetSortedBy,CategoryFilterData } from "../../hooks/utilis/index";
import { useFillter } from "../../hooks/context/product-context";
import { useWishlist } from "../../hooks/context/wishlist-context";
import { addToWishlistService,removeFromWishlistService } from "../../services/index"

export default function ProductWithFilter() {
  const [productData, setProductData] = useState([]);
  const {productState} = useFillter();
  const {sortBy,
    maxPrice,
    productRating,
    productDiscount,
    solidJacket,
    thinJacket,
    lightWeightJacket}=productState;
  const sortedData = GetSortedBy(productData,sortBy);
  const categoryData=CategoryFilterData(sortedData,{solidJacket,thinJacket,lightWeightJacket})
  const filteredData = GetFilteredData(categoryData,{maxPrice,productRating,productDiscount});
   useEffect(() => {
    (async function () {
      try {
        const response=await axios.get("/api/products");
        if(response.status === 200){
          setProductData(response.data.products);
      }
      else{
          throw new Error();
      }
      } catch (error) {
        console.error(error);
      }
    })();
  },[]);
  const [InWishlist, setInWishlist] = useState(false);
  const { wishlist, setWishlist } = useWishlist();

	const CalladdToWishlistServer = async () => {
		try {
			const res = await addToWishlistService(product, auth.token);
			if (res.status === 201) {
				setWishlist((prev) => ({
					...prev,
					wishlistProducts: res.data.wishlist,
				}));
			}
		} catch (err) {
			console.log(err.response);
		}
	};
  return (
    <div className="filter-page">
      <section className="filter-product-container">
        <Productfilter />
      </section>
      <section className="products-list">
        <div className="section-title">
          <h2>our products</h2>
        </div>
        <div className="products-middle">
          {filteredData.map(({
             _id,
             image,
             name,
             subtitle,
             rating,
             price,
             discount,
        })=>(
          <ProductsList
            key={_id}
            productFilterImg={image}
            imgAlt={"product"}
            title={name}
            productSubtitle={subtitle}
            starRating={rating}
            productPrice={price}
            productDiscount={discount}
          />
    ))}
        </div>
      </section>
    </div>
  );
}
