import { createContext, useContext, useReducer, useState } from "react";
import axios from "axios";
import { AuthReducer } from "../reducer/auth-reducer";

const AuthContext=createContext();
const intialObj={
    user:{},
    status:'',
    newUser:{}
}
const AuthProvider = ({ children }) => {
    const [authState,dispatch] = useReducer(AuthReducer, intialObj);
    const login=async (email,password)=>{
        try {
          const response=await axios.post("/api/auth/login",{email,password})
          if(response.status === 200){
            localStorage.setItem("userData",response.data.encodedToken);
            localStorage.setItem("userData",JSON.stringify(response.data.foundUser));
            dispatch({type:"SET_USER",payload:response.data.foundUser});
            dispatch({type:"SET_STATUS",payload:response.status});
          setTimeout(()=>{
            navigate("/");
          },1000);
          
          }
          
        } catch (error) {
          console.error(error)
        }
      }
    return (
      <AuthContext.Provider value={{authState,dispatch,login,status}}>
        {children}
      </AuthContext.Provider>
    );
  };
  const useAuth = () => useContext(AuthContext);
  
  export { AuthProvider, useAuth };
