import { createContext, useContext, useEffect, useReducer} from "react";
import axios from "axios";
import { AuthReducer } from "../reducer/auth-reducer";
const intialObj={
  user:"",
  token:"",
}
const AuthContext=createContext(null);
const AuthProvider = ({ children }) => {
    const [authState,authDispatch] = useReducer(AuthReducer, intialObj);
    useEffect(()=>{
      const token = localStorage.getItem("token");
      const user = JSON.parse(localStorage.getItem("user"));
      authDispatch({ type: "CHECK_USER", payload: { user, token } });
    },[])
    const loginHandler = async (event) => {
      event.preventDefault();
      try {
        const response = await axios.post("/api/auth/login", user);
        if (response.status === 200) {
  
          localStorage.setItem("token", response.data.encodedToken);
          localStorage.setItem("user", JSON.stringify(response.data.foundUser));
  
          authDispatch({ type: "LOGIN", payload: { user: response.data.foundUser, token: response.data.encodedToken } })
  
          navigate("/");
        }
        else if (response.status === 404) {
          alert("Email not found");
        }
        else if (response.status === 401) {
          alert("Wrong Password");
        }
        else if (response.status === 500) {
          alert("Server Error");
        }
      }
      catch (error) {
        console.log(error);
      }
    }
    return (
      <AuthContext.Provider value={{authState,authDispatch,loginHandler}}>
        {children}
      </AuthContext.Provider>
    );
  };
  const useAuth = () => useContext(AuthContext);
  
  export { AuthProvider, useAuth };
