import "../authentication.css";
import axios from 'axios';
import { Link } from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
import { useState } from "react";
export default function loginPage() {
const Login=()=>{
  const {authDispatch}=useAuth();
  const [user,setUser]=useState({
    email:"",
    password:"",
  });
  const guestUser = {
    email: "guest@gmail.com",
    password: "guest@23"
  }
  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value })
  }

  const guestHandler = (e) => {
    e.preventDefault();
    setUser(guestUser);
  }
  const loginHandler = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("/api/auth/login", userData);
      if (response.status === 200) {

        localStorage.setItem("token", response.data.encodedToken);
        localStorage.setItem("userData", JSON.stringify(response.data.foundUser));

        authDispatch({ type: "LOGIN", payload: { userData: response.data.foundUser, token: response.data.encodedToken } })

        navigate("/");
      }
      else if (response.status === 404) {
        alert("Email not found");
      }
      else if (response.status === 401) {
        alert("Wrong Password,try again");
      }
      else if (response.status === 500) {
        alert("Server Error");
      }
    }
    catch (error) {
      console.error(error);
    }
  }
}
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input 
            type="email" 
            name="useremail" 

            />
            <label>Email address </label>
          </div>
          <div className="user-box">
            <input 
            type="password" 
            name="password" 
            value={userData.password}
            onChange={changeHandler}
            />
            <label>Password</label>
          </div>
          <div className="password-box">
            <div className="div-inline">
              <div className="password-save">
                <input type="checkbox" name="remember-me" />
                <label for="remember-me">Remember me</label>
              </div>
              <Link to="/forgot-password">
              <div className="forgot-pwd">
                <span className="forgot-label">forgot password ?</span>
              </div>
              </Link>
            </div>
            <button type="submit" className="login-submit" onClick={loginHandler}>
            Add Guest credentials
            </button>
            <button type="submit" className="login-submit">
              Login
            </button>
            <Link to="/signup">
            <button className="create-account">Create New Account </button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}
