const intialAuthState={user:'',token:''};
const AuthReducer = (authState, action) => {
    switch (action.type) {
        case 'SIGNUP':
            return {
                ...authState,
               user:action.payload.user,
                token:action.payload.token
            }
        case 'LOGIN':
            return {
                ...authState,
                user:action.payload.user,
                token:action.payload.token
            }
        case 'LOGOUT':
            return {
                    ...intialAuthState,
            }
        default:
        return authState;
    }
}
export {AuthReducer}
