import "../authentication.css";
import { Link } from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
import {useNavigate} from "react-router-dom";
import { useState } from "react";
export default function loginPage() {
  const navigate=useNavigate();
  const {login}=useAuth();
  const [isLoggedIn,setIsLoggedIn]=useState({
    email:"",
    password:""
  });
  const handleSubmit=(e)=>{
    e.preventDefault();
    setIsLoggedIn({isLoggedIn})
  }
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input 
            type="email" 
            name="useremail" 
            placeholder="enter email"
            value={isLoggedIn.email}
            onChange={(e)=>
              setIsLoggedIn({...isLoggedIn,email:e.target.value})
            }
            />
            <label>Email address </label>
          </div>
          <div className="user-box">
            <input 
            type="password"
             name="password" 
             value={isLoggedIn.password}
             onChange={(e)=>
            setIsLoggedIn({...isLoggedIn,password:e.target.value})
            }
             />
            <label>Password</label>
          </div>
          <div className="password-box">
            <div className="div-inline">
              <div className="password-save">
                <input type="checkbox" name="remember-me" />
                <label for="remember-me">Remember me</label>
              </div>
              <Link to="/forgot-password">
              <div className="forgot-pwd">
                <span className="forgot-label">forgot password ?</span>
              </div>
              </Link>
            </div>
            <button 
            type="submit" 
            className="login-submit"
            onClick={(e)=>{
              e.preventDefault();
              setIsLoggedIn({
                email:"shivani@gmail.com",
                password:"shivani"
              })
            }}
            >
            Add Guest credentials
            </button>
            <button 
            type="submit" 
            className="login-submit"
            onClick={(e)=>handleSubmit()}
            >
              Login
            </button>
            <Link to="/signup">
            <button className="create-account">Create New Account </button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}
