export const AuthReducer = (authState, action) => {
    switch (action.type) {
        case 'SIGNUP':
            return {
                ...authState,
               user:action.payload.user,
                token:action.payload.token
            }
        case 'LOGIN':
            return {
                ...authState,
               user:action.payload.user,
                token:action.payload.token
            }
        case 'LOGOUT':
            return {
                    ...authState,
                   user:null,
                    token:null
            }
        case 'CHECK_USER':
            return {
                    ...authState,
                   user: action.payload.user,
                    token: action.payload.token
            }
        default:
        return authState;
    }
}
