import "./style.css";
import {useNavigate,useParams} from "react-router-dom";
import {Navbar} from "../../components/index";
import { productImg1 } from "../../assets/images/photos";
import { useAuth,useCart,useWishList } from "../../hooks";
import {addToWishlistHandler,addToCartHandler,removeFromWishlistHandler,getSingleProductHandler} from "../../hooks/utilis/index";
import { useEffect, useState } from "react";
export default function SingleProductPage(){
  const navigate=useNavigate();
  const { productId }=useParams();
  //  const {_id,
  //   image,
  //   name,
  //   subtitle,
  //   rating,
  //   price,
  //   discount,}=product
  const [product,setProduct]=useState({_id,
    image,
    name,
    subtitle,
    rating,
    price,
    discount,});
  const {authState:{token}}=useAuth();
  const {cartState:{cart},cartDispatch}=useCart();
  const {wishlistState:{wishlist}}=useWishList();
  const checkWishlistAction=(_id)=>{
    const item=wishlist.find(item=>item._id ===_id);
    return item ? "GO TO WISHLIST":"ADD TO WISHLIST";
  }
  const callAddTowishlistHandler=(_id)=>{
    if(token){
      addToWishlistHandler(product,wishlistDispatch,token)
    }else{
      navigate("/login")
    }
  }
  const checkWishlistHandler=(_id)=>{
    return checkWishlistAction(_id)==="ADD TO WISHLIST" ? callAddTowishlistHandler(_id):navigate("/wishlist")
  }
  const checkCartAction=(_id)=>{
    const item=cart.find(item=>item._id===_id);
    return item ? "GO TO CART":"ADD TO CART";
  }
  const callAddtocartHandler=(_id)=>{
    if(token){
      addToCartHandler(product,cartDispatch,token)
    }else{
      navigate("/login")
    }
  }
  const checkCartHandler=(_id)=>{
    return checkCartAction(_id)==="ADD TO CART"?callAddtocartHandler(_id):navigate("/cart")
  }
  useEffect(()=>getSingleProductHandler(productId,setProduct),[])
    return (
      <>
      <Navbar/>
        <article className="two-col-container">
        <div className="single-img1 align-center">
          <img src={product.image} alt="product" className="single-image" />
        </div>
        <div className="div">
          <div className="align-center">
            <div className="btn-container">
            <h3> 
              {product.name}
            </h3>
          <p>
          {product.subtitle}
          </p>
        <h4> Rs.{product.price} </h4>
        <span> {product.discount} offer<button className="rating-btn">{product.rating} â˜…</button></span> 
            </div>
          </div>
        <div className="btn-container">
        <button className="cart-btn"
          onClick={()=>checkCartHandler(product._id)}
          >
            <i className="fas fa-shopping-cart"></i>
            {checkCartAction(product._id)}
          </button>
          <button className=" wish-btn"
          onClick={()=>checkWishlistHandler(product._id)}
          >
            <i className="fa-solid fa-heart"></i>
            {checkWishlistAction(product,_id)}
          </button>
        </div>
        </div>
      </article> 
      </>
    )
}
