import { removeFromCartService } from "../../../services"

export const removeFromCartHandler = async (_id, token, cartDispatch) => {
    try {
        const res = await removeFromCartService(_id, token);
        if (res.status === 200) {
            cartDispatch({ type: "REMOVE_FROM_CART", payload: res.data.cart });
        }
        else {
            throw new Error();
        }
    }
    catch (error) {
        alert(error);
    }
}
