import "../authentication.css";
import axios from 'axios';
import { Link } from "react-router-dom";
import { useAuth } from "../../../hooks/context/auth-context";
import { useState } from "react";
export default function loginPage() {
  const navigate = useNavigate();
  const { authDispatch } = useAuth();
  const [user, setUser] = useState({
    email: "",
    password: "",
  })

  const guestUser = {
    email: "adarshbalika@gmail.com",
    password: "adarshbalika"
  }

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value })
  }

  const guestHandler = (e) => {
    e.preventDefault();
    setUser(guestUser);
  }

  const loginHandler = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/auth/login", user);
      if (res.status === 200) {

        localStorage.setItem("token", res.data.encodedToken);
        localStorage.setItem("user", JSON.stringify(res.data.foundUser));

        authDispatch({ type: "LOGIN", payload: { user: res.data.foundUser, token: res.data.encodedToken } })

        navigate("/");
      }
      
      else if (res.status === 401) {
        alert("Wrong Password");
      }
      else if (res.status === 404) {
        alert("Email not found");
      }
      else if (res.status === 500) {
        alert("Server Error");
      }
    }
    catch (error) {
      console.error(error);
    }
  }
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input 
            type="email" 
            name="useremail" 
            value={user.email}
            onChange={changeHandler}
            />
            <label>Email address </label>
          </div>
          <div className="user-box">
            <input 
            type="password" 
            name="password" 
            value={user.password}
            onChange={changeHandler}
            />
            <label>Password</label>
          </div>
          <div className="password-box">
            <div className="div-inline">
              <div className="password-save">
                <input type="checkbox" name="remember-me" />
                <label for="remember-me">Remember me</label>
              </div>
              <Link to="/forgot-password">
              <div className="forgot-pwd">
                <span className="forgot-label">forgot password ?</span>
              </div>
              </Link>
            </div>
            <button type="submit" className="login-submit" onClick={guestHandler} >
            Add Guest credentials
            </button>
            <button type="submit" className="login-submit" onClick={loginHandler}>
              Login
            </button>
            <Link to="/signup">
            <button className="create-account">Create New Account </button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}
