import "./signup.css";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
import { useAuth } from "../../../hooks/context/auth-context";
export default function SignupPage() {
  const [user, setUser] = useState({
		firstName: "",
    lastName:"",
		email: "",
		password: "",
    confirmPassword:""
	});

	const { setIsAuth, setToken, navigate } = useAuth();

	const signupHandler = async (e, user) => {
		e.preventDefault();
		try {
			const response = await axios.post("/api/auth/signup", user);
			localStorage.setItem("isAuth", true);
			localStorage.setItem("token", response.data.encodedToken);
			setToken(response.data.encodedToken);
			setIsAuth(true);
			navigate("/");
		} catch (err) {
			console.error("error", err);
		}
	};
  return (
    <div className="loginbox-align">
      <div className="login-box">
        <h2>SignUp</h2>
        <h4>Please fill in this form to create an account.</h4>
        <form>
          <div className="user-box">
            <input
             type="text" 
             name="firstName"

              />
            <label>First Name</label>
          </div>
          <div className="user-box">
            <input
             type="text" 
             name="lastName"

              />
            <label>Last Name</label>
          </div>
          <div className="user-box">
            <input 
            type="email" name="gmail" />
            <label>Gmail</label>
          </div>
          <div className="user-box">
            <input 
            type="password"
             name="password" 
             />
            <label>Create Password</label>
          </div>
          <div className="user-box">
            <input 
            type="password" 
            name="confirm-password"
             />
            <label>Re-Enter Password</label>
          </div>
          <p className="form-terms">
            <input type="checkbox" />
            By creating an account you agree to{" "}
            <a href="sample.com">our Terms</a> & Privacy.
          </p>
          <button type="submit" className="signup-btn">
            SignUp
          </button>
          <Link to="/login">
          <button className="already-account">Already have an account </button>
          </Link>
        </form>
      </div>
    </div>
  );
}
