import React from "react";
import "./cart.css";
import { CartAmount } from "./component/CartAmount";
import { CartItem } from "./component/CartItem";
import { useCart } from "../../hooks/context/cart-context";
import { getCartItemService } from "../../services";
import { updateCartHandler,removeFromCartHandler,cartTotalPriceHandler } from "../../hooks/utilis";

export default function Cart() {
  const {cartState,cartDispatch}=useCart();
  const {cart}=cartState;
  const {cartQuantity,cartItemPrice,cartTotal}=cartTotalPriceHandler(cart);
  const getUpdateCartHandler=(_id,actionType)=>{

    const callUpdateCartHandler = (_id, actionType) => {
    updateCartHandler(_id, actionType, token, cartDispatch);
  }

  const callRemoveFromCartHandler = (_id) => {
    removeFromCartHandler(_id, token, cartDispatch)
  }

  const getCartItems = async () => {
    try {
      const response = await getCartItemsService(token);
      if (response.status === 200) {
        cartDispatch({ type: "GET_CART", payload: response.data.cart })
      } else {
        throw new Error();
      }
    }
    catch (error) {
      alert(error);
    }
  }

  useEffect(() => getCartItems(), []);

  }
  return (
    <div className="cart-container">
      <h2>your cart({cartQuantity})</h2>
      <div className="cart">
        <div className="cart-content">
          <CartItem
            cartImg={productImg1}
            imgAlt={"product"}
            CartTitle={"Mast &Harbour"}
            CartSubtitle={"Women Blue Solid Bomber Jacket"}
            cartPrice={"2699"}
            cartDiscount={"50%"}
          />
        </div>
        <div className="cart-footer">
          <h3>Price details</h3>
          <hr />
          <CartAmount 
          cartItems={"2"} 
          cartItemPrice='1000'
          cartDiscount={"20%"}
          cartCharge={"200"}
          cartTotal={'1000'}
          />
        </div>
      </div>
    </div>
  );
}
